DROP TABLE IF EXISTS "answers"; CREATE TABLE "answers" ("id" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL , "question" INTEGER, "answer" VARCHAR); INSERT INTO "answers" VALUES(1,1,"Ответик"); DROP TABLE IF EXISTS "questions"; CREATE TABLE "questions" ("id" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL , "task" INTEGER, "number" INTEGER, "type" INTEGER, "question" VARCHAR); INSERT INTO "questions" VALUES(1,1,1,1,"Вопросик"); DROP TABLE IF EXISTS "scene"; CREATE TABLE "scene" ("id" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL , "name" VARCHAR, "time" INTEGER); INSERT INTO "scene" VALUES(1,"Пробный",1800); DROP TABLE IF EXISTS "scene-list"; CREATE TABLE "scene-list" ("id" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL , "scene" INTEGER, "num" INTEGER, "task" INTEGER); INSERT INTO "scene-list" VALUES(1,1,1,1); DROP TABLE IF EXISTS "tasks"; CREATE TABLE "tasks" ("id" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL  UNIQUE , "title" VARCHAR, "content" TEXT, "img" VARCHAR, "type" INTEGER); INSERT INTO "tasks" VALUES(1,"Первое задание","Современная образовательная парадигма, ратифицируя приоритет           личностной ориентации педагогического процесса, в ходе которого           осуществляется развитие природных задатков, заложенных в каждом индивидууме,           требует переосмысления существующих традиционных форм и           методов общеобязательного образования.","img/davinci.jpg",""); 